var kCountryCode="";function addCommas(num){return String(num).replace(/(\d)(?=(\d\d\d)+(?!\d))/g,"$1,")}function drawMap(){Highcharts.mapChart("gc-map",{chart:{map:"custom/world",backgroundColor:"transparent"},title:{text:null},legend:{enabled:!1},colors:["#f64"],credits:{enabled:!1},series:[{data:[[kCountryCode,1]]}]})}function drawMapWithData(code){kCountryCode=code.toLowerCase(),drawMap()}